import Buttons from '@/components/buttons/button.vue';
export default {
  name: 'LoginPage',
  components: {
    Buttons: Buttons
  },
  data: function data() {
    return {
      errorMessage: '',
      errorMessageEmail: '',
      email: '',
      emailSuccess: '',
      errorCount: 0,
      countErrorMessage: '',
      hasEmpty: false,
      hasErrors: false,
      hasSucceeded: false,
      password: '',
      passwordSuccess: '',
      terdaftar: false,
      userData: [{
        user: 'email@email.com',
        pass: '123qwe123'
      }, {
        user: 'email1@email.com',
        pass: '123qwe123'
      }, {
        user: 'email2@email.com',
        pass: '123qwe123'
      }, {
        user: 'email3@email.com',
        pass: '123qwe123'
      }]
    };
  },
  methods: {
    check: function check() {
      var _self = this;

      var email = _self.userData.filter(function (ob) {
        return ob['user'].toLowerCase().includes(_self.email.toLowerCase());
      });

      var password = _self.userData.filter(function (ob) {
        return ob['pass'].toLowerCase().includes(_self.password.toLowerCase());
      });

      if (email != '' && password != '') {
        return true;
      } else {
        return false;
      }
    },
    inputClassesEmail: function inputClassesEmail() {
      var classes = ['appearance-none', 'border', 'border-blue-royal-500', 'font-pt-sans', 'leading-tight', 'mb-4', 'p-4', 'rounded-sm', 'text-base', 'text-gray-600', 'w-full', 'focus:outline-none'];
      if (this.hasErrors) classes.push('border-red-thunderbird-700 bg-red-thunderbird-100');
      return classes.join(' ');
    },
    inputClassesPassword: function inputClassesPassword() {
      var classes = ['appearance-none', 'border', 'border-none', 'font-pt-sans', 'leading-tight', 'p-4', 'rounded-sm', 'text-base', 'text-gray-600', 'w-full', 'focus:outline-none'];
      if (this.hasEmpty) classes.push('border-red-thunderbird-700 bg-red-thunderbird-100');
      return classes.join(' ');
    },
    isStrongPass: function isStrongPass(password) {
      var regExp = /(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%&*()]).{10,}/;
      var validPassword = regExp.test(password);
      return validPassword;
    },
    myFunction: function myFunction() {
      var x = document.getElementById("inputPassword");

      if (x.type === "password") {
        x.type = "text";
      } else {
        x.type = "password";
      }
    },
    submit: function submit() {
      if (this.email === '') {
        this.hasErrors = true;
        this.errorMessageEmail = 'Email harus diisi';
      } else {
        this.hasErrors = false;
      }

      if (this.password === '') {
        this.hasEmpty = true;
        this.errorMessage = 'Kata sandi harus diisi. ';
      } else {
        this.hasEmpty = false;
      }

      if (this.email != '' && this.password != '') {
        if (this.validateEmail(this.email)) {
          if (this.check()) {
            console.log('user udah ada');
            this.hasSucceeded = true;
          } else {
            console.log('user belum ada');
            this.terdaftar = true;
            this.errorCount += 1;
            this.countErrorMessage = 'Maaf, email atau kata sandi Anda salah. ';

            if (this.errorCount >= 3) {
              this.countErrorMessage = 'Demi keamanan, 3 kali gagal masuk, Anda harus menunggu 30 menit untuk mencoba kembali.';
            }

            if (this.errorCount >= 5) {
              this.countErrorMessage = 'Maaf, Anda telah 5 kali gagal masuk, silahkan menunggu 30 menit untuk mencoba lagi.';
            }
          }
        } else {
          if (this.validateEmail(this.email) === false) {
            this.hasErrors = true;
            this.errorMessageEmail = 'Masukkan format email yang benar.';
          }
        }
      }
    },
    validateEmail: function validateEmail(email) {
      var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
      return re.test(String(email).toLowerCase());
    }
  }
};
