  methods: {
    inputClassesEmail() {
      let classes = [
        'appearance-none',
        'border',
        'border-blue-royal-500',
        'font-pt-sans',
        'leading-tight',
        'mb-4',
        'p-4',
        'rounded-sm',
        'text-base',
        'text-gray-600',
        'w-full',
        'focus:outline-none',
      ]

      if (this.hasErrors) classes.push('border-red-thunderbird-700 bg-red-thunderbird-100')
      return classes.join(' ')
    },
    check(){
      const _self = this
      var filtered = _self.userData.filter(function(ob) {
        return ob['user'].toLowerCase().includes(_self.email.toLowerCase());
      });
      if(filtered != ''){
        return false
      }else{
        return true
