        'w-full',
        'focus:outline-none',
      ]

      if (this.hasEmpty) classes.push('border-red-thunderbird-700 bg-red-thunderbird-100')
      return classes.join(' ')
    },
    isStrongPass(password) {
        var regExp = /(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%&*()]).{10,}/;
        var validPassword = regExp.test(password);
        return validPassword;
    },
    myFunction() {
      var x = document.getElementById("inputPassword");
      if (x.type === "password") {
          x.type = "text";
      } else {
          x.type = "password";
      }
    },
    submit() {
      const _self = this
      if(this.email ===''){
          this.hasErrors = true
          this.errorMessageEmail = 'Email harus diisi'
      }else{
        this.hasErrors = false
      }
      if (this.password === ''){
        this.hasEmpty = true
        this.errorMessage = 'Kata sandi harus diisi. '
      }else{
        this.hasEmpty = false
      }

      if(this.email !='' && this.password != ''){
        console.log(this.isStrongPass(this.password))
        if(this.validateEmail(this.email)){
          if(this.check()) {
              console.log('user belum ada')
              this.hasSucceeded = true
            }else{
                console.log('user udah ada')
                this.terdaftar = true
          }
        }else{
          if(this.validateEmail(this.email) === false){
            this.hasErrors = true
            this.errorMessageEmail = 'Masukkan format email yang benar.'
          }
          if(this.isStrongPass(this.password) === false){
            this.hasErrors = true
            this.errorMessage = 'Kata sandi harus sesuai yang ditentukan.'
          }
        }
      }
    },
    validateEmail(email) {
      var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
      return re.test(String(email).toLowerCase());
    }
  }
