import Buttons from '@/components/buttons/button.vue'

export default {
  name: 'PasswordFirstStep',
  components:{
    Buttons,
  },
  data()
  {
    return {
      hasErrors: false,
      hasSucceeded: false,
      email: '',
      emailSuccess:''
    }
  },
  methods: {
    inputClassesEmail() {
      let classes = [
        'appearance-none',
        'border',
        'border-blue-royal-500',
        'font-pt-sans',
        'leading-tight',
        'mb-2',
        'p-4',
        'rounded-sm',
        'text-base',
        'text-gray-450',
        'w-full',
        'focus:outline-none',
      ]

      if (this.hasErrors) classes.push('border-red-thunderbird-700 bg-red-thunderbird-100')
      return classes.join(' ')
    },
    submit() {
      if (this.email === ''){
        this.hasErrors = true
      }else{
        this.hasSucceeded = true
      }
    }
  }
}
