export default {
  name: 'InputBox',
  data() {
    return {
      password: '',
      passwordSuccess:'',
      items: [
        {
          caption: 'Masukan password baru',
          id: 'password',
          type: 'password',
        }
      ]
    }
  },
  methods:{
    inputClassesPassword() {
      let classes = [
        'appearance-none',
        'border',
        'border-none',
        'font-pt-sans',
        'leading-tight',
        'p-4',
        'rounded-sm',
        'text-base',
        'text-gray-600',
        'w-full',
        'focus:outline-none',
      ]

      if (this.hasEmpty) classes.push('border-red-thunderbird-700 bg-red-thunderbird-100')
      return classes.join(' ')
    },
    isStrongPass(password) {
        var regExp = /(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%&*()]).{10,}/;
        var validPassword = regExp.test(password);
        return validPassword;
    },
    myFunction() {
      var x = document.getElementById("inputPassword");
      if (x.type === "password") {
          x.type = "text";
      } else {
          x.type = "password";
      }
    },
  }
}
